// Filterable/Exportable viewer fed by Flask uploads
const flask = msg.payload || {};
const uploads = Array.isArray(flask.uploads) ? flask.uploads : [];
const deviceStats = {};
for (const u of uploads) {
  const id = u.device_id || 'Unknown-Device';
  if (!deviceStats[id]) deviceStats[id] = { totalRecords: 0, firstSeen: u.timestamp, lastSeen: u.timestamp };
  deviceStats[id].totalRecords++;
  deviceStats[id].lastSeen = u.timestamp;
}

const q = (msg.req && msg.req.query) || {};
const deviceId = q.device || '';
const limit = parseInt(q.limit) || 25;
const offset = parseInt(q.offset) || 0;
const format = q.format || 'html';
const sortBy = q.sort || 'timestamp';
const order = q.order || 'desc';

let rows = uploads.slice();
if (deviceId) rows = rows.filter(r => (r.device_id||'') === deviceId);

rows.sort((a,b)=>{
  let av,bv;
  switch (sortBy) {
    case 'deviceId': av=(a.device_id||''); bv=(b.device_id||''); break;
    case 'payloadSize': av=(a.bytes||0); bv=(b.bytes||0); break;
    case 'timestamp':
    default: av=new Date(a.timestamp); bv=new Date(b.timestamp); break;
  }
  return order==='asc' ? (av>bv?1:-1) : (av<bv?1:-1);
});

const totalRecords = rows.length;
const paginated = rows.slice(offset, offset+limit);

if (format === 'json') {
  msg.payload = {
    metadata: { total: totalRecords, limit, offset, deviceFilter: deviceId, sortBy, order, source: 'flask:/api/uploads' },
    devices: Object.keys(deviceStats).map(id => ({ deviceId: id, ...deviceStats[id] })),
    data: paginated.map(r => ({ deviceId: r.device_id, timestamp: r.timestamp, bytes: r.bytes, samples: r.samples, flask_uploaded: true })),
    generatedAt: new Date().toISOString()
  };
  msg.headers = { 'Content-Type': 'application/json' };
  return msg;
}

if (format === 'csv') {
  let csv = 'DeviceID,Timestamp,Bytes,NumSamples\n';
  for (const r of paginated) {
    csv += `"${r.device_id||''}","${r.timestamp||''}","${r.bytes||0}","${Array.isArray(r.samples)?r.samples.length:0}"\n`;
  }
  msg.payload = csv;
  msg.headers = { 'Content-Type': 'text/csv', 'Content-Disposition': 'attachment; filename="ecowatt-flask-data.csv"' };
  return msg;
}

const deviceOptions = Object.keys(deviceStats).map(id => `<option value="${id}" ${deviceId===id?'selected':''}>${id}</option>`).join('');
const dataRows = paginated.map(r => {
  const num = Array.isArray(r.samples)? r.samples.length : 0;
  const preview = Array.isArray(r.samples)? r.samples.slice(0,8).map(s=>s.value).join(', ') : 'N/A';
  return `
    <tr>
      <td><strong>${r.device_id||''}</strong></td>
      <td>${new Date(r.timestamp).toLocaleString()}</td>
      <td>${r.bytes||0} bytes</td>
      <td>${num}</td>
      <td><details><summary>Preview</summary><div style="margin-top:8px;font-family:monospace;">${preview}</div></details></td>
    </tr>`;
}).join('');

const prevOffset = Math.max(0, offset - limit);
const nextOffset = offset + limit;
const hasNext = nextOffset < totalRecords;
