msg.headers = { 'Content-Type': 'text/html' };
return msg;

// Dedicated benchmarks component from Flask's BENCHMARKS (already shows min/avg/max)
const flask = msg.payload || {};
const benchmarks = Array.isArray(flask.benchmarks) ? flask.benchmarks : [];

const rows = benchmarks.map((b, i) => `
