const deviceRows = Object.entries(deviceStatus).map(([deviceId, status]) => {
  const progress = totalChunks > 0 ? ((status.chunk_received + 1) / totalChunks * 100).toFixed(1) : 0;
  const verifiedIcon = status.verified ? '‚úÖ' : '‚ùå';
  const verifiedText = status.verified ? 'Verified' : 'Not Verified';

  return '<tr>' +
    '<td><strong>' + deviceId + '</strong></td>' +
    '<td>' + (status.chunk_received + 1) + ' / ' + totalChunks + '</td>' +
    '<td>' +
      '<div style="background:#ecf0f1;border-radius:8px;height:20px;overflow:hidden;">' +
        '<div style="background:#667eea;height:100%;width:' + progress + '%;"></div>' +
      '</div>' +
      '<span style="font-size:0.9em;color:#6c757d;">' + progress + '%</span>' +
    '</td>' +
    '<td>' + verifiedIcon + ' ' + verifiedText + '</td>' +
    '<td>' + new Date(status.last_update).toLocaleString() + '</td>' +
  '</tr>';
}).join('');

