// EcoWatt Binary Delta Compression Generator
// Generates binary payload for Flask API

// Get device info
const reqHeaders = msg.req.headers || {};
const deviceId = reqHeaders['device-id'] || reqHeaders['Device-ID'] || 'EcoWatt-Unknown';

// Simulate realistic EcoWatt sensor data
const generateSensorData = () => {
    const baseVoltage = 220.0;
    const baseCurrent = 5.0;
    const baseFrequency = 50.0;

    const samples = [];
    for (let i = 0; i < 8; i++) {
        samples.push({
            voltage: baseVoltage + (Math.random() - 0.5) * 2,
            current: baseCurrent + (Math.random() - 0.5) * 1,
            frequency: baseFrequency + (Math.random() - 0.5) * 0.5
        });
    }
    return samples;
};

// Generate sensor readings
const sensorData = generateSensorData();
const voltageValues = sensorData.map(s => s.voltage);
const currentValues = sensorData.map(s => s.current);
const frequencyValues = sensorData.map(s => s.frequency);

// Create binary delta payload for Flask API
function createDeltaPayload(values, reg_addr) {
    const bufferParts = [];

    // Base timestamp (4 bytes, little-endian uint32)
    const baseTimestamp = Math.floor(Date.now() / 1000);
    const tsBuffer = Buffer.allocUnsafe(4);
    tsBuffer.writeUInt32LE(baseTimestamp, 0);
    bufferParts.push(tsBuffer);

    // Register address (1 byte) - 0x01=voltage, 0x02=current, 0x03=frequency
    const regBuffer = Buffer.from([reg_addr]);
    bufferParts.push(regBuffer);

    // Base value (4 bytes, little-endian float)
    const baseValue = values[0];
    const valBuffer = Buffer.allocUnsafe(4);
    valBuffer.writeFloatLE(baseValue, 0);
    bufferParts.push(valBuffer);

    // Delta samples (2 bytes delta_t + 4 bytes delta_v)
    let prevValue = baseValue;

    for (let i = 1; i < values.length; i++) {
        // Delta time (15 minutes = 900 seconds, as uint16)
        const deltaT = 900;
        const dtBuffer = Buffer.allocUnsafe(2);
        dtBuffer.writeUInt16LE(Math.min(deltaT, 65535), 0);
        bufferParts.push(dtBuffer);

        // Delta value (scaled by 100, as int32)
        const deltaV = Math.round((values[i] - prevValue) * 100);
        const dvBuffer = Buffer.allocUnsafe(4);
        dvBuffer.writeInt32LE(deltaV, 0);
        bufferParts.push(dvBuffer);

        prevValue = values[i];
    }

    return Buffer.concat(bufferParts);
}

try {
    // Generate binary payload for voltage data (register 0x01)
    const binaryPayload = createDeltaPayload(voltageValues, 0x01);

    // Store readable data for dashboard
    const readableData = {
        deviceId: deviceId,
        timestamp: new Date().toISOString(),
        data: {
            voltage: voltageValues,
            current: currentValues, 
            frequency: frequencyValues
        },
        binarySize: binaryPayload.length,
        compressionRatio: (voltageValues.length * 4 * 3) / binaryPayload.length
    };

    // Store in global context for dashboard
    let storedData = global.get('ecowatt_deviceData') || [];
    const storageRecord = {
        id: `${deviceId}_${Date.now()}`,
        deviceId: deviceId,
        timestamp: new Date().toISOString(),
        storedAt: new Date().toISOString(),
        data: readableData.data,
        validation: {
            payloadSize: binaryPayload.length,
            validated: true
        },
        compression: {
            wasCompressed: true,
            algorithm: 'delta',
            compressionRatio: readableData.compressionRatio.toFixed(2),
            originalSize: voltageValues.length * 4 * 3,
            compressedSize: binaryPayload.length
        },
        quality: {
            score: 98,
            issues: []
        }
    };

    storedData.push(storageRecord);
    if (storedData.length > 1000) {
        storedData = storedData.slice(-1000);
    }
    global.set('ecowatt_deviceData', storedData);

    // Update device stats
    let deviceStats = global.get('ecowatt_deviceStats') || {};
    if (!deviceStats[deviceId]) {
        deviceStats[deviceId] = {
            totalRecords: 0,
            firstSeen: storageRecord.timestamp,
            lastSeen: storageRecord.timestamp
        };
    }
    deviceStats[deviceId].totalRecords += 1;
    deviceStats[deviceId].lastSeen = storageRecord.timestamp;
    global.set('ecowatt_deviceStats', deviceStats);

    // Prepare for Flask upload
    msg.payload = binaryPayload;
    msg.headers = {
        'Content-Type': 'application/octet-stream',
        'Content-Length': binaryPayload.length,
        'device-id': deviceId
    };
    msg.url = 'http://localhost:5000/api/upload';
    msg.method = 'POST';

    // Store metadata for response
    msg.originalData = readableData;
    msg.storageRecord = storageRecord;

    node.status({fill: "blue", shape: "dot", text: `Generated ${binaryPayload.length} bytes`});

    return msg;

} catch (error) {
    msg.payload = {
        status: 'error',
        message: 'Binary generation failed: ' + error.message,
        timestamp: new Date().toISOString()
    };
    msg.statusCode = 500;

    node.error(`Binary generation failed: ${error.message}`);
    node.status({fill: "red", shape: "dot", text: "Generation failed"});

    return [null, msg];
}

// Handle Flask API response
const flaskResponse = msg.payload;
const originalData = msg.originalData;
const storageRecord = msg.storageRecord;

if (flaskResponse && flaskResponse.status === 'success') {
    // Success response
    msg.payload = {
        status: 'success',
        message: 'EcoWatt data uploaded to Flask successfully',
        cloud: {
            flask_status: flaskResponse.status,
            flask_received: flaskResponse.received,
            device_id: originalData.deviceId,
            samples_count: originalData.data.voltage.length,
            binary_size: originalData.binarySize,
            compression_ratio: originalData.compressionRatio.toFixed(2)
        },
        local_storage: {
            record_id: storageRecord.id,
            stored_at: storageRecord.storedAt,
            dashboard_ready: true
        },
        timestamp: new Date().toISOString()
    };

    msg.statusCode = 200;
    node.status({fill: "green", shape: "dot", text: `Flask OK: ${flaskResponse.received}B`});

} else {
    // Error response
    msg.payload = {
        status: 'error',
        message: 'Flask upload failed',
        error: flaskResponse,
        local_backup: {
            record_id: storageRecord.id,
            stored_locally: true,
            dashboard_available: true
        },
        timestamp: new Date().toISOString()
    };

    msg.statusCode = 500;
    node.status({fill: "yellow", shape: "dot", text: "Flask failed, local OK"});
}

return msg;

