// EcoWatt Data Viewer - Detailed data analysis
// Compatible with Flask API integration

const storedData = global.get('ecowatt_deviceData') || [];
const deviceStats = global.get('ecowatt_deviceStats') || {};

// Parse query parameters
const queryParams = msg.req.query || {};
const deviceId = queryParams.device || null;
const limit = parseInt(queryParams.limit) || 25;
const offset = parseInt(queryParams.offset) || 0;
const format = queryParams.format || 'html';
const sortBy = queryParams.sort || 'timestamp';
const order = queryParams.order || 'desc';

// Apply filters
let filteredData = storedData;
if (deviceId) {
    filteredData = storedData.filter(record => record.deviceId === deviceId);
}

// Sort data
filteredData.sort((a, b) => {
    let aVal, bVal;
    switch(sortBy) {
        case 'deviceId':
            aVal = a.deviceId;
            bVal = b.deviceId;
            break;
        case 'payloadSize':
            aVal = a.validation ? a.validation.payloadSize : 0;
            bVal = b.validation ? b.validation.payloadSize : 0;
            break;
        case 'timestamp':
        default:
            aVal = new Date(a.timestamp);
            bVal = new Date(b.timestamp);
            break;
    }

    if (order === 'asc') {
        return aVal > bVal ? 1 : -1;
    } else {
        return aVal < bVal ? 1 : -1;
    }
});

// Pagination
const totalRecords = filteredData.length;
const paginatedData = filteredData.slice(offset, offset + limit);

node.warn(`Data Viewer: Found ${totalRecords} total records, showing ${paginatedData.length}`);

