        csv += `"${record.id}","${record.deviceId}","${record.timestamp}","${record.storedAt}","${record.validation.payloadSize}","${record.compression.wasCompressed}","${record.compression.compressionRatio}","${avgVoltage}","${avgCurrent}","${avgFrequency}"\n`;
    });

